"use client";

import { ThemeProvider as NextThemesProvider } from "next-themes";
import { useEffect, useState } from "react";

export function ThemeProvider({
    children,
    ...props
}: React.ComponentProps<typeof NextThemesProvider>) {
    const [mounted, setMounted] = useState(false);

    useEffect(() => {
        setMounted(true);
    }, []);

    if (!mounted) {
        // Return null or children without theming to avoid hydration mismatch
        // Returning children ensures content is visible but might flash, 
        // giving we want "premium" feel, we can prevent flash or just accept it.
        // Standard practice for next-themes is often just rendering, but suppressHydrationWarning is already on html.
        // Let's return children to be safe for SEO/content, themes apply fast.
        return <>{children}</>;
    }

    return <NextThemesProvider {...props}>{children}</NextThemesProvider>;
}

import { motion } from 'framer-motion';
import { Contact, OrbitLayer } from '@/lib/store';

interface OrbitNodeProps {
    contact: Contact;
    orbit: OrbitLayer;
    index: number;
    totalInOrbit: number;
    radius: number;
    onDragEnd: (contactId: string, point: { x: number; y: number }) => void;
}

export function OrbitNode({ contact, orbit, index, totalInOrbit, radius, onDragEnd }: OrbitNodeProps) {
    // Calculate position on the circle
    // Distribute evenly
    const angle = (2 * Math.PI * index) / totalInOrbit;
    const x = radius * Math.cos(angle);
    const y = radius * Math.sin(angle);

    // Colors based on drift status
    const statusColors = {
        stable: 'border-emerald-500 bg-emerald-100 text-emerald-700',
        drifting: 'border-amber-400 bg-amber-100 text-amber-700',
        fading: 'border-rose-400 bg-rose-100 text-rose-700',
        ghost: 'border-slate-300 bg-slate-100 text-slate-400 opacity-60',
    };

    const statusColor = statusColors[contact.driftStatus || 'stable'] || statusColors.stable;

    return (
        <motion.div
            drag
            dragElastic={0.2}
            dragMomentum={false}
            onDragEnd={(e, info) => onDragEnd(contact.id, info.point)}
            initial={{ x: 0, y: 0, scale: 0 }}
            animate={{ x, y, scale: 1 }}
            className={`absolute w-12 h-12 -ml-6 -mt-6 rounded-full border-2 flex items-center justify-center shadow-md cursor-pointer z-10 ${statusColor}`}
            whileDrag={{ scale: 1.2, zIndex: 50 }}
            whileTap={{ scale: 1.1 }}
            whileHover={{ scale: 1.1 }}
        >
            {/* Simple Avatar with Initials */}
            <span className="font-bold text-sm">
                {contact.name.charAt(0).toUpperCase()}
            </span>

            {/* Tooltip on Hover (Simple absolute positioning) */}
            <div className="opacity-0 hover:opacity-100 absolute -bottom-8 left-1/2 -translate-x-1/2 bg-slate-800 text-white text-[10px] px-2 py-1 rounded whitespace-nowrap pointer-events-none transition-opacity">
                {contact.name} ({contact.healthScore}%)
            </div>
        </motion.div>
    );
}

import { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useStore, Contact, OrbitLayer } from '@/lib/store';
import { OrbitNode } from './OrbitNode';

export function OrbitView() {
    const { contacts, setOrbit } = useStore();
    const containerRef = useRef<HTMLDivElement>(null);

    // Dimensions
    // const [center, setCenter] = useState({ x: 0, y: 0 });
    const centerX = 0; // Relative to container center if we translate
    const centerY = 0;

    // Group contacts by orbit
    const innerContacts = contacts.filter(c => c.orbit === 'inner');
    const middleContacts = contacts.filter(c => c.orbit === 'middle');
    const outerContacts = contacts.filter(c => c.orbit === 'outer' || !c.orbit); // Default to outer

    // Ring Radii
    const innerRadius = 80;
    const middleRadius = 160;
    const outerRadius = 240;

    const handleDragEnd = (contactId: string, point: { x: number; y: number }) => {
        // point is relative to the node's starting position (which is on the ring).
        // Wait, framer-motion drag returns info.point which is page/client coordinates usually, 
        // BUT if we use layout animations it might be tricky.
        // Let's rely on simple distance calculation from the center of the container.

        if (!containerRef.current) return;

        // Get container center
        const rect = containerRef.current.getBoundingClientRect();
        const containerCenterX = rect.left + rect.width / 2;
        const containerCenterY = rect.top + rect.height / 2;

        // Calculate distance from center
        // info.point is client coordinates
        const dropX = point.x;
        const dropY = point.y;

        const dist = Math.sqrt(Math.pow(dropX - containerCenterX, 2) + Math.pow(dropY - containerCenterY, 2));

        let newOrbit: OrbitLayer = 'outer';
        if (dist < 120) newOrbit = 'inner';
        else if (dist < 200) newOrbit = 'middle';
        else newOrbit = 'outer';

        setOrbit(contactId, newOrbit);
    };

    return (
        <div ref={containerRef} className="w-full h-[600px] relative overflow-hidden flex items-center justify-center bg-slate-50/50 dark:bg-slate-900/50 backdrop-blur-3xl rounded-[2.5rem] border border-slate-200 dark:border-white/5 shadow-inner">
            {/* Background Ambience */}
            <div className="absolute inset-0 bg-gradient-radial from-violet-500/10 via-transparent to-transparent opacity-50 pointer-events-none" />

            {/* The Solar System Container */}
            <div className="relative w-0 h-0">

                {/* Sun / User Avatar */}
                <div className="absolute -ml-8 -mt-8 w-16 h-16 bg-gradient-to-br from-violet-600 to-indigo-600 rounded-full z-20 shadow-lg shadow-violet-500/40 flex items-center justify-center text-white border-4 border-white dark:border-slate-800 relative">
                    <span className="font-bold text-xl">ME</span>
                    {/* Glowing effect in dark mode */}
                    <div className="absolute inset-0 rounded-full bg-violet-500/50 blur-xl -z-10 animate-pulse" />
                </div>

                {/* Orbit Rings */}
                {/* Inner Ring */}
                <div className="absolute rounded-full border-2 border-dashed border-violet-200 dark:border-violet-500/20" style={{ width: innerRadius * 2, height: innerRadius * 2, marginLeft: -innerRadius, marginTop: -innerRadius }} />

                {/* Middle Ring */}
                <div className="absolute rounded-full border-2 border-dashed border-indigo-100 dark:border-indigo-500/10" style={{ width: middleRadius * 2, height: middleRadius * 2, marginLeft: -middleRadius, marginTop: -middleRadius }} />

                {/* Outer Ring */}
                <div className="absolute rounded-full border-2 border-dashed border-slate-100 dark:border-white/5" style={{ width: outerRadius * 2, height: outerRadius * 2, marginLeft: -outerRadius, marginTop: -outerRadius }} />

                {/* Nodes */}
                <AnimatePresence>
                    {/* Inner Orbit */}
                    {innerContacts.map((contact, i) => (
                        <OrbitNode
                            key={contact.id}
                            contact={contact}
                            orbit="inner"
                            index={i}
                            totalInOrbit={innerContacts.length}
                            radius={innerRadius}
                            onDragEnd={handleDragEnd}
                        />
                    ))}

                    {/* Middle Orbit */}
                    {middleContacts.map((contact, i) => (
                        <OrbitNode
                            key={contact.id}
                            contact={contact}
                            orbit="middle"
                            index={i}
                            totalInOrbit={middleContacts.length}
                            radius={middleRadius}
                            onDragEnd={handleDragEnd}
                        />
                    ))}

                    {/* Outer Orbit */}
                    {outerContacts.map((contact, i) => (
                        <OrbitNode
                            key={contact.id}
                            contact={contact}
                            orbit="outer"
                            index={i}
                            totalInOrbit={outerContacts.length}
                            radius={outerRadius}
                            onDragEnd={handleDragEnd}
                        />
                    ))}
                </AnimatePresence>

            </div>

            {/* Legend / Info */}
            <div className="absolute bottom-6 left-6 p-4 bg-white/80 dark:bg-black/60 backdrop-blur-xl rounded-2xl text-xs text-slate-500 dark:text-slate-400 border border-slate-100 dark:border-white/5 shadow-lg">
                <div className="font-bold text-slate-700 dark:text-slate-200 mb-2">Orbit Layers</div>
                <div className="flex items-center gap-2 mb-1"><span className="w-2 h-2 rounded-full bg-violet-400"></span> Inner (Intimate)</div>
                <div className="flex items-center gap-2 mb-1"><span className="w-2 h-2 rounded-full bg-indigo-300"></span> Middle (Friends)</div>
                <div className="flex items-center gap-2"><span className="w-2 h-2 rounded-full bg-slate-200 dark:bg-slate-600"></span> Outer (Network)</div>
                <div className="mt-2 text-[10px] text-slate-400 dark:text-slate-500 pt-2 border-t border-slate-100 dark:border-white/5">Drag to move orbits.</div>
            </div>

            {/* Quick Add Button */}
            {/* Quick Add Button */}
            <button
                onClick={() => window.location.href = '/add'}
                className="absolute bottom-6 right-6 w-14 h-14 bg-primary text-primary-foreground rounded-full flex items-center justify-center shadow-xl hover:scale-105 active:scale-95 transition-all z-30"
            >
                <span className="text-3xl font-light mb-1">+</span>
            </button>

            {contacts.length === 0 && (
                <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 text-center pointer-events-none">
                    <p className="text-slate-400 dark:text-slate-600 text-sm font-medium">No contacts in orbit.</p>
                </div>
            )}
        </div>
    );
}
